const _runCat = {
  init: () => {
    _runCat.initAnimation();
  },
  initAnimation: () => {
    // width screen
    var widthScreen = state.boxWidth - state.offer.track.width() - state.offer.cat.width();

    // init gsap timeline 
    const setLasso = gsap.timeline();
    const setWord = gsap.timeline();
    const seToStartTrackAnimation = gsap.timeline();
    const setCatFace = gsap.timeline();

    const widthLasso = state.boxWidth - state.offer.cat.width() - state.offer.track.width() + 40;


    //set laso
    setLasso
      .set(state.offer.lasso, {
        opacity: 0,
        width: 0,
      })
    //set word
    setWord
      .set(state.offer.word, {
        opacity: 0,
        y: 20,
      })


    // begin lasso
    const isLasso = () => {
      setLasso
        .to(state.offer.lasso, {
          width: widthLasso,
          ease: Elastic.easeOut,
          duration: 1.5,
          opacity: 1,
        })
        .from(state.offer.word, {
          opacity: 0,
        })
        .to(state.offer.word, {
          width: widthLasso,
          y: 0,
          opacity: 1,
          onComplete: () => {
            setTimeout(() => {
              isWordFinish();
              
            },800);
            setTimeout(() => {
              isTruckFinish();
              isLassoFinish();
            }, 2400);
          }
        })
    }
    // finish lasso 
    const isLassoFinish = () => {
      setLasso
        .to(state.offer.lasso, {
          ease: Power3.easeOut,
          duration: 2,
          opacity: 0,
          width: '100px',
          onComplete: () => {
            setCatFace
              .to(state.offer.catFace, {
                ease: Back.easeOut,
                fill: 'black',
                duration: 3,
              });

          }
        })
    }
    // start truck
    const isTruck = () => {
      
      var controller = new ScrollMagic.Controller();
      var tl = new TimelineMax();
      tl.to(state.offer.track, {
        ease: Power4.easeOut,
        duration: 2,
        x: -widthScreen,
        onComplete: () => {
          isLasso();
        }
      });

      var scene = new ScrollMagic.Scene({
          triggerElement: "#cat-animation",
          triggerHook: 0.5,
          reverse: false
        })
        .setTween(tl)
        .addTo(controller);

    }

    const isTruckFinish = () => {
      seToStartTrackAnimation
        .set(state.offer.track, {
          x: -widthScreen,
        })
        .to(state.offer.track, {
          x: 0,
          ease: Power3.easeOut,
          duration: 2,
        });
    }

    const isWordFinish = () => {
      setWord
        .from(state.offer.word, {
          opacity: 1,
          y: 0,

        })
        .to(state.offer.word, {
          opacity: 0,
          duration: .5,
          y: -20,
        })
    }
    const eyeCat = () =>{
      var eye = $(".js-cat-eye"),
      eye_2 = $(".js-cat-eye-2");
        var tl2 = new TimelineMax({
          repeat: 0,
        });

       var tl1 = new TimelineMax({
          repeat: -1,
        });

      tl2.from(eye, {
        autoAlpha: 1,
        visibility:"visible",
        ease: Elastic.easeOut
      });
      
      tl2.to(eye, {
        duration: 1.5,
        visibility:"hidden",
        autoAlpha: 0,
        ease: Elastic.easeOut,
      });
      
      tl1.from(eye_2, {
        autoAlpha: 0,
        duration: 2,
        y: 0,
        ease: Elastic.easeOut,
      });
      
      tl1.to(eye_2, {
        duration: 1.5,
        autoAlpha: 1,
        y: -4,
        ease: Elastic.easeOut,
      });
    }

    eyeCat();
    isTruck();
  }
}

